<div class="language-vue line-numbers-mode" data-ext="vue"><pre v-pre class="language-vue"><code>   // 地图echart的初始化
    renderChart3(highlightCity) {
      // 先判断是否已经存在echart，不存在再初始化
      let charts = echarts.getInstanceByDom(document.getElementById("chart3"));
      if (charts == null) {
        charts = echarts.init(document.getElementById("chart3"));
      }

      const getHighlightColor = (value) => {
        const max = Math.max(...highlightCity.map((item) => item.value));
        const min = Math.min(...highlightCity.map((item) => item.value));

        const range = max - min;
        const percentage = (value - min) / range;
        const hue = 60; // 黄色的色调，可根据需要微调
        const saturation = 100; // 饱和度，这里设为100，即全饱和
        const lightness = 50 - 20 * percentage; // 亮度，根据比例动态计算

        const color = `hsl(${hue}, ${saturation}%, ${lightness}%)`; // 黄色渐变

        return color;
      };
      const options = {
        // 背景颜色
        backgroundColor: "white",
        // 提示浮窗样式
        tooltip: {
          show: true,
          trigger: "item",
          alwaysShowContent: true,
          backgroundColor: "rgba(50, 50, 50, 0.7)",
          borderWidth: 0,
          borderColor: "rgb(51, 51, 51);",
          borderRadius: 4,
          hideDelay: 100,
          triggerOn: "mousemove",
          enterable: true,
          textStyle: {
            color: "rgb(255, 255, 255);",
            fontSize: "12",
            width: 20,
            height: 30,
            overflow: "break",
          },
          showDelay: 100,
        },
        // 地图配置
        geo: {
          map: "china",
          label: {
            // 通常状态下的样式
            show: false,
            textStyle: {
              color: "#fff",
            },
          },
          // 地图区域的样式设置
          itemStyle: {
            // borderColor: "rgba(147, 235, 248, 1)",
            borderColor: "white",
            borderWidth: 1,
            areaColor: "lightskyblue",
          },
       
          emphasis: {
            itemStyle: {
              areaColor: (params) => {
                const value = params.value;
                return getHighlightColor(value);
              },
              borderWidth: 0,
            },
           label: {
              show: false,
              textStyle: {
                color: "#fff",
              },
            },
          },
        },
        grid: {
          left: "0px",
          top: "0px",
          right: "0px",
          bottom: "0px",
        },
        series: [
          {
            name: "",
            type: "map",
            map: "china",
            data: highlightCity,

            emphasis: {
              itemStyle: {
                areaColor: "#fcce10",
                borderWidth: 0,
              },
              label: {
                show: true,
                textStyle: {
                  color: "#fff",
                },
              },
            },
            show: false,
          },
        ],
      };
      // 地图注册，第一个参数的名字必须和option.geo.map一致
      echarts.registerMap("china", China);
      charts.setOption(options);

      let highLight = [];
      highlightCity.map((item) => {
        highLight.push(item.name);
      });

      for (let i = 0; i &lt; highLight.length; i++) {
        charts.dispatchAction({
          type: "highlight",
          name: highLight[i],
        });
      }
    },

