        xiaB<span class="token punctuation">.</span>bottom <span class="token operator">=</span> <span class="token string">'20rem'</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>
    <span class="token function">$</span><span class="token punctuation">(</span><span class="token string">'.xia_img-box'</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">animate</span><span class="token punctuation">(</span>xiaB<span class="token punctuation">,</span> <span class="token number">1000</span><span class="token punctuation">,</span> rowBack<span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
