        <span class="token keyword">var</span> <span class="token function-variable function">postaction</span> <span class="token operator">=</span> <span class="token keyword">function</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span> <span class="token punctuation">}</span>  <span class="token comment">//此处增加了一个postaction函数</span>
