      <span class="token selector">75%</span><span class="token punctuation">{</span><span class="token property">background-image</span><span class="token punctuation">:</span>~<span class="token string">"url(@{url}06.png)"</span><span class="token punctuation">}</span>
      <span class="token selector">87.5%</span><span class="token punctuation">{</span><span class="token property">background-image</span><span class="token punctuation">:</span> ~<span class="token string">"url(@{url}07.png)"</span><span class="token punctuation">}</span>
